using Chronos.Core;

namespace Chronos.Coins
{
  public sealed class Coin : ZES.Infrastructure.Domain.AggregateRoot
  {
    public Coin() 
    {
      Register<Chronos.Coins.Events.CoinCreated>(ApplyEvent);
    }  
    public Coin(string name, string ticker) : this() 
    {
      When(new Chronos.Coins.Events.CoinCreated(name, ticker));
    }  
    public string Name
    {
      get; 
      set;
    }  
    public string Ticker
    {
      get; 
      set;
    }
    public Asset Asset => new Asset(Name, Ticker, AssetType.Coin);
    private void ApplyEvent (Chronos.Coins.Events.CoinCreated e)
    {
      Id = e.Name;
      Name = e.Name;
      Ticker = e.Ticker;
    }
  }
}

